#! /bin/bash
exec /usr/bin/qemu-system-x86_64 -enable-kvm -name Windows -smp cpus=2,sockets=1,cores=2,threads=1 -cpu host -machine q35,accel=kvm -m 4096 -vga qxl -vnc :1 -nographic -drive file=/path/to/OVMF_CODE.secboot.fd,if=pflash,format=raw,unit=0,readonly=on -drive file=/path/to/OVMF_VARS.secboot.fd,if=pflash,format=raw,unit=1 -drive file=/dev/vg/windows,id=sata0,if=none,aio=threads,format=raw,cache=writethrough -device ich9-ahci,id=ahci -device ide-drive,drive=sata0,bus=ahci.0 -netdev tap,helper=/usr/libexec/qemu-bridge-helper,id=net0 -device virtio-net-pci,netdev=net0,id=vmnic -device virtio-tablet,wheel-axis=true -device ich9-intel-hda -device intel-iommu -device virtio-rng-pci -smbios type=0,vendor="vendor",version="version",date="date",release="0",uefi=on -smbios type=1,manufacturer="manufacture",product="product",version="Not Specified",serial="serial",uuid="uuid",sku="sku",family="family" -acpitable file=/sys/firmware/acpi/tables/SLIC -acpitable file=/sys/firmware/acpi/tables/MSDM -debugcon file:debug.log -global isa-debugcon.iobase=0x402 $@
